// jsx 的样式控制
// 1. 行内样式 - 在元素身上绑定style属性
// 2. 类名样式

import './ass.css'

const style = {
  color:'red',
  fontSize:'50px'
}

function App() {
  return (
    <div className="App">
      <span style={style}>this is span</span>
    </div>
  );
}

export default App;

// 结尾笔记
